document.addEventListener("DOMContentLoaded", function() {
    const quranDiv = document.getElementById("quran");
    quranDiv.innerHTML = quranText.replace(/\n/g, "<br>");

    const increaseFontButton = document.getElementById("increaseFont");
    const decreaseFontButton = document.getElementById("decreaseFont");

    increaseFontButton.addEventListener("click", function() {
        changeFontSize(1);
    });

    decreaseFontButton.addEventListener("click", function() {
        changeFontSize(-1);
    });

    function changeFontSize(delta) {
        const currentFontSize = parseInt(window.getComputedStyle(quranDiv).fontSize);
        quranDiv.style.fontSize = (currentFontSize + delta) + "px";
    }

    const surahList = document.getElementById("surahList");
    audioSurahs.forEach(surah => {
        const listItem = document.createElement("li");
        listItem.textContent = `${surah.name} (${surah.englishName})`;
        listItem.addEventListener("click", function() {
            playSurah(surah.url);
        });
        surahList.appendChild(listItem);
    });

    document.getElementById('setSleepTimer').addEventListener('click', function() {
        const minutes = document.getElementById('sleepTimer').value;
        if (minutes && minutes > 0) {
            setSleepTimer(minutes);
        } else {
            alert('н—ћм ≈ѕќ«б Џѕѕ ’ЌнЌ гд «бѕё«∆ё.');
        }
    });

    function setSleepTimer(minutes) {
        const milliseconds = minutes * 60 * 1000;
        setTimeout(() => {
            stopQuranPlayback();
            alert(' г ≈нё«Ё «б ‘џнб »”»» «д е«Ѕ гƒё  «бджг.');
        }, milliseconds);
    }

    function stopQuranPlayback() {
        const audioPlayer = document.getElementById("audioPlayer");
        audioPlayer.pause();
    }
});

function playSurah(url) {
    const audioPlayer = document.getElementById("audioPlayer");
    const audioSource = document.getElementById("audioSource");
    audioSource.src = url;
    audioPlayer.load();
    audioPlayer.play();
}

function showSection(sectionId) {
    document.querySelectorAll('.section').forEach(section => {
        section.style.display = 'none';
    });
    document.getElementById(sectionId).style.display = 'block';
}
